package com.company;import java.util.HashMap;import java.util.Map;import java.util.Set;/*** Created by Hoshiko on 11/15/15.*/public class Vertex {    public String getName()    {        return name;    }    String name;    Map<Vertex, Integer> neighborsToWeights;    public Vertex(String name)    {        this.name = name;        this.neighborsToWeights = new HashMap<Vertex, Integer>();    }    public Set<Vertex> getNeighbors()    {        return this.neighborsToWeights.keySet();    }    @Override    public String toString(){        return name;    }}